import React from 'react';
import './enterNewTweet.css';
import {Textarea, Button} from '../../../form/input';
import DisplayTweets from '../displaytweets/displayTweets';
import * as OldTweets from '../../../../data/getAllTimeline';

class EnterNewTweet extends React.Component {

    constructor () {
        super();
        this.state = {
            buttonClicked: false,
            user: "",
            date: "",
            content: "",
            tweetArr: []
        }
        //this.tweetArr = [];

        console.log('constructor')
    }

    componentDidMount() {
        //첫 화면에 보일 데이터를 페치..fetch
        console.log('componentDidMount')

        let previousTweets = OldTweets.default.result;
        this.setState({
            tweetArr: previousTweets
        })

    }

    getPreviousTweets = () => {
        let previousTweets = OldTweets.default.result;
        for (let i=0; i < previousTweets.length; i++) {
            this.tweetArr.push(this.makeTweetList(previousTweets[i]));
        }
        console.log(this.tweetArr);
    }

    getDate = () => {
        let now = new Date();
        let y = now.getFullYear();
        let m = now.getMonth() + 1;
        let d = now.getDate();
        return `${y}${(m < 10 ? '0' : '') + m}${(d < 10 ? '0' : '') + d}`;
    }

    generateNewTweet = () => {
        this.setState({
            buttonClicked: !this.state.buttonClicked,
            user : document.querySelector('.UserName').innerHTML,
            date : this.getDate(),
            content : document.querySelector('.NewMessage').value
        });

        console.log(this.makeTweetList(this.state));


    }

    tweet = () => {
        const newList = this.state.tweetArr.slice();
        newList.push({
            contents: 'sdfsf'
        });

        this.setState({
            tweetArr: newList
        })
    }

    render() {
        return (
            <div className="EnterNewTweet">
                <Textarea 
                    className="NewMessage" 
                    placeholder="What's happening?" 
                />
                <Button 
                    className="Make-Btn" 
                    name="MakeTweetBtn"
                    innerHTML="Tweet"
                    btnClicked={this.generateNewTweet}
                />

                <DisplayTweets />

                {
                    this.state.tweetArr.map(el=> (
                        <li>
                            <span className="User">{el.user}</span>
                            <span className="Date">{el.date}</span>
                            <div className="Content">{el.contents}</div>
                        </li>
                    ))
                }
            </div>
        );
    }
}

export default EnterNewTweet;
