import React from 'react';

class Button extends React.Component {

    constructor() {
        super();

        this.state = {
            clicked: false
        }

        this.handleClick = this.handleClick.bind(this);
    }

    handleClick() {
        console.log(123);
        this.setState ({
            clicked: !this.state.clicked
        });
    }

    render() {
        let className = this.props.className;
        let innerHTML = this.props.innerHTML;

        return (
            <button 
                className={className}
                onClick={this.handleClick}
            >
                {innerHTML}
            </button>
        );
    }
}

class Textarea extends React.Component {

    constructor() {
        super();

        this.state = {
            clicked: false
        }
    }

    render() {
        let className = this.props.className;
        let placeholder = this.props.placeholder;

        return (
            <textarea className={className} placeholder={placeholder}/>
        );
    };
}

class Input extends React.Component {

    constructor() {
        super();
    }

    render() {
        let className = this.props.className;
        let type = this.props.type;
        let placeholder = this.props.placeholder;
        let autocomplete = this.props.autocomplete;

        return (
            <input className={className} type={type} placeholder={placeholder} autocomplete={autocomplete}/>
        );
    }
}

export {
    Input,
    Button,
    Textarea,
}
