import React from 'react';
import './enterNewTweet.css';
import {Textarea, Button} from '../../../form/input';
import DisplayTweets from '../displaytweets/displayTweets';

class EnterNewTweet extends React.Component {

    constructor () {
        super();
        this.state = {
            buttonClicked: false
        }
    }

    getDate = () => {
        let now = new Date();
        let y = now.getFullYear();
        let m = now.getMonth() + 1;
        let d = now.getDate();
        return `${y}${(m < 10 ? '0' : '') + m}${(d < 10 ? '0' : '') + d}`;
    }

    generateNewTweet = () => {
        this.setState({
            buttonClicked: !this.state.buttonClicked
        });



        let tweetObj = {
            user : document.querySelector('.UserName')
        }


    }

    render() {
        return (
            <div className="EnterNewTweet">
                <Textarea 
                    className="NewMessage" 
                    placeholder="What's happening?" 
                />
                <Button 
                    className="Make-Btn" 
                    name="MakeTweetBtn"
                    innerHTML="Tweet"
                    btnClicked={this.generateNewTweet}
                />
                <DisplayTweets />
            </div>
        );
    }
}

export default EnterNewTweet;
