import React from 'react';
import './enterNewTweet.css';
import {Textarea, Button} from '../../../form/input';
import DisplayTweets from '../displaytweets/displayTweets';

class EnterNewTweet extends React.Component {

    constructor () {
        super();
        this.state = {
            buttonClicked: false
        }
        this.tweetArr = [];
    }

    getPreviousTweets = () => {
        fetch ('/data/getAllTimeline.json')
            .then (
            (response) => {
                response.json().then((data) => {
                    for (let i=0; i<data.result.length; i++) {
                    const originalTweet = document.createElement('li');
                        originalTweet.innerHTML = makeTweetList(data.result[i]);
                    elTweetList.appendChild(originalTweet);
                    }
                    });
                }
            )
    }

    getDate = () => {
        let now = new Date();
        let y = now.getFullYear();
        let m = now.getMonth() + 1;
        let d = now.getDate();
        return `${y}${(m < 10 ? '0' : '') + m}${(d < 10 ? '0' : '') + d}`;
    }

    generateNewTweet = () => {
        this.setState({
            buttonClicked: !this.state.buttonClicked
        });

        let tweetObj = {
            user : document.querySelector('.UserName').innerHTML,
            date : this.getDate(),
            content : document.querySelector('.NewMessage').value
        }

        this.tweetArr.unshift(this.makeTweetList(tweetObj));

    }

    makeTweetList = (obj) => {
        let tweet = 
        `
          <span class="user">${obj.user}</span>
          <span class="date">${obj.date}</span>
          <div class="content">${obj.contents}</div>
        `
        return tweet;
    };

    render() {
        return (
            <div className="EnterNewTweet">
                <Textarea 
                    className="NewMessage" 
                    placeholder="What's happening?" 
                />
                <Button 
                    className="Make-Btn" 
                    name="MakeTweetBtn"
                    innerHTML="Tweet"
                    btnClicked={this.generateNewTweet}
                />
                <DisplayTweets />
            </div>
        );
    }
}

export default EnterNewTweet;
